var chartInput = {
    "series": ["X"],
    "data": [[]],
    "labels": []
};

// Avoid that the pie disappears (for main cpu value 0)
if (msg.payload.main.cpu === 0) msg.payload.main.cpu = 0.1;

// Add the cpu of the main process first
chartInput.data[0].push(msg.payload.main.cpu);
chartInput.labels.push(msg.payload.main.pid + " (main)");

// Add the cpu of all child processes
msg.payload.children.forEach(function(child) {
    // Avoid that the pie disappears (for child cpu value 0)
    if (child.cpu === 0) child.cpu = 0.1;
    chartInput.data[0].push(child.cpu);
    chartInput.labels.push(child.pid + " (child)");
});

return { payload:[chartInput] };

